mapper.select:
SELECT *
FROM reservation
WHERE reservation_id = #{reservationId}
  AND member_id = #{memberId}

SELECT
reservation_id AS id,
reservation_start,
reservation_end,
vehicle_number AS carNumber,
fee,
status
FROM reservation
WHERE member_id = #{memberId}

    AND reservation_start >= #{startDate}


    AND reservation_end <= #{endDate}


    AND status = #{status}


    AND vehicle_number LIKE CONCAT('%', #{keyword}, '%')

ORDER BY reservation_start DESC
LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(*)
FROM reservation
WHERE member_id = #{memberId}

    AND reservation_start >= #{startDate}


    AND reservation_end <= #{endDate}


    AND status = #{status}


    AND vehicle_number LIKE CONCAT('%', #{keyword}, '%')

SELECT COUNT(*)
FROM reservation
WHERE member_id = #{memberId}
  AND DATE(reservation_start) = #{date}
  AND status = 'PAID'

SELECT COUNT(*)
FROM reservation
WHERE member_id = #{memberId}
AND status IN ('PAID', 'CONFIRMED')
AND
(
    reservation_start <= #{end}
    AND reservation_end >= #{start}
)
